mapper.select:
select u.*, c.name c_name, c.code c_code, c.count c_count, c.level c_level, c.date c_date, c.term c_term from users u left join company c on c.id = u.companyid

    
        and u.id = #{id}
    
    
        and u.username = #{username}
    
    
        and u.password = #{password}
    
    
        and u.email = #{email}
    
    
        and u.phone = #{phone}
    
    
        and c.id = #{company.id}

select u.*, c.name c_name, c.code c_code, c.count c_count, c.level c_level, c.date c_date, c.term c_term from users u left join company c on c.id = u.companyid

    
        and u.name like #{name}
    
    
        and c.id = #{company.id}
    

order by u.username

select u.id, u.name, u.username, count(distinct p.id) pcount, count(distinct q.id) qcount, p.date
from users u
         left join project p on p.userid = u.id and p.date between #{date1} and #{date2}
         left join manhole q on q.projectid = p.id
where find_in_set(p.state, #{scope}) and u.companyid = #{company.id}
group by u.id, p.date
